import React, { Component} from 'react';
import './App.css';
import Video from './video';
import Camera, { FACING_MODES, IMAGE_TYPES } from 'react-html5-camera-photo';
import 'react-html5-camera-photo/build/css/index.css';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {call: "End Call", hold: "Hold", holdDisplay: false, img: ""};

        // This binding is necessary to make `this` work in the callback
        this.endCall = this.endCall.bind(this);
        this.pauseCall = this.pauseCall.bind(this);
    }

    onTakePhoto (dataUri) {
        console.log('takePhoto');
        this.setState({
            img: dataUri
        });
    }

    onCameraError (error) {
        console.error('onCameraError', error);
    }

    onCameraStart (stream) {
        console.log('onCameraStart');
    }

    onCameraStop () {
        console.log('onCameraStop');
    }

    endCall(){
        console.log("endCall");
        if (this.state.call === "End Call"){
            this.setState({
                holdDisplay: true,
                call: 'Call'
            });
        }
        else {
            this.setState({
                holdDisplay: false,
                hold: "Hold",
                call: 'End Call'
            });
        }
    }

    pauseCall(){
        console.log("pauseCall");
        if (this.state.hold === "Hold"){
            this.setState({
                hold: 'Resume'
            });
        }
        else{
            this.setState({
                hold: 'Hold'
            });
        }
    }

    render() {
        const holdStyle = this.state.holdDisplay ? {display: 'none'} : {};
        return (
            <React.Fragment>
                  <div className="App">
                      <button onClick = {this.endCall}>{this.state.call}</button>
                      <button onClick ={this.pauseCall} style={holdStyle}>{this.state.hold}</button>
                  </div>
                <div className="App">
                    <Camera
                        onTakePhoto = { (dataUri) => { this.onTakePhoto(dataUri); } }
                        onCameraError = { (error) => { this.onCameraError(error); } }
                        idealFacingMode = {FACING_MODES.ENVIRONMENT}
                        idealResolution = {{width: 640, height: 480}}
                        imageType = {IMAGE_TYPES.JPG}
                        imageCompression = {0.97}
                        isMaxResolution = {false}
                        isImageMirror = {false}
                        isDisplayStartCameraError = {true}
                        sizeFactor = {1}
                        onCameraStart = { (stream) => { this.onCameraStart(stream); } }
                        onCameraStop = { () => { this.onCameraStop(); } }
                    />
                    <img src={this.state.img}></img>
                </div>
            </React.Fragment>

        );
     }
}

export default App;
