import React, { Component} from 'react';
import './App.css';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {hold: "Hold", holdDisplay: true};

        // This binding is necessary to make `this` work in the callback
        this.endCall = this.endCall.bind(this);
        this.pauseCall = this.pauseCall.bind(this);
    }

    endCall(){
        console.log("endCall");
        this.setState({
            holdDisplay: false
        });
    }

    pauseCall(){
        console.log("pauseCall");
        if (this.state.hold === "Hold"){
            this.setState({
                hold: 'Resume'
            });
        }
        else{
            this.setState({
                hold: 'Hold'
            });
        }

    }

    render() {
        const style = this.state.holdDisplay ? {display: 'none'} : {}
        return (
          <div className="App">
              <button onClick = {this.endCall}>End Call</button>
              <button onClick ={this.pauseCall}>{this.state.hold}</button>
          </div>
        );
     }
}

export default App;
